import React,{useEffect} from "react";
import {
  Text,
  View,
  StyleSheet,
  TouchableOpacity,
} from "react-native";
import { ListItem, Icon } from "react-native-elements";

import RoundedImage from "../../components/profile/main/RoundedImage";

import {useDispatch,useSelector} from "react-redux";

import AsyncStorage from '@react-native-async-storage/async-storage';

import * as Notifications from 'expo-notifications';

import * as authenServices from '../../services/Authen';

import * as authenActions from "../../../store/actions/Authen";
import * as homeActions from "../../../store/actions/Home";

const categoryProfile = [
  {
    name: "Trang mạng xã hội",
    icon: "facebook",
    type_icon: "material-community",
  },
  {
    name: "Kết nối ứng dụng",
    icon: "transit-connection-variant",
    type_icon: "material-community",
  },
  {
    name: "Ứng dụng đã được kết nối",
    icon: "connectdevelop",
    type_icon: "font-awesome-5",
  },
  {
    name: "Đổi mật khẩu",
    icon: "lock-reset",
    type_icon: "material-community",
  },
  {
    name: "Đăng xuất",
    icon: "exit-to-app",
    type_icon: "material-community",
  }
  // more items
];

export function ProfileScreen({navigation}) {
  const dispatch = useDispatch();

  const token = useSelector((state) => state.authen.token);

  const profile=useSelector((state) => state.profile.profile);


  return (
    <View style={styles.container}>
      <View style={styles.infoView}>
        <RoundedImage source={{uri: profile[0].AnhSV ==="" ? undefined : profile[0].AnhSV}}></RoundedImage>
        <Text style={styles.nameText}>{profile[0].HoTen}</Text>
      </View>
      
      <View style={styles.nameText}>
        {categoryProfile.map((item, index) => (
          <ListItem key={index}  bottomDivider>
            <TouchableOpacity style={styles.itemBtn} onPress={() =>{
              if(index === 1){
                navigation.navigate("Connect application");
              }
              else if(index === 2){
                navigation.navigate("Web Customed"); 
              }
              else if(index === 3){
                navigation.navigate("Change Password");
              }
              else if(index === 4){
                AsyncStorage.removeItem('tokenValue').then(async () => {
                  await authenServices.SignOut(token);
                  dispatch(authenActions.logout);
                  dispatch(homeActions.VisibleBotTab(false));
                  Notifications.cancelAllScheduledNotificationsAsync();
                  navigation.navigate("Login");
                })
              }
            }}>
              <Icon name={item.icon} type={item.type_icon} color='#333333'/>
              <ListItem.Content>
                <ListItem.Title style={styles.itemName}>
                  {item.name}
                </ListItem.Title>
              </ListItem.Content>
              <ListItem.Chevron />
            </TouchableOpacity>
          </ListItem>
        ))}
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },

  nameText: {
    marginTop: 20,
  },

  infoView: {
    alignItems: "center",

  },

  itemBtn: {
    flex: 1,
    flexDirection: "row",
    height:"100%"
  },

  itemName: {
    paddingLeft: 10,
  },

  headerTitle: {
    fontSize:18,
    color:"#FFFFFF"
  },

  signoutBtn: {
    alignItems: "center",
    padding: 15,
    marginVertical: 20,
    marginHorizontal:10,
    borderRadius: 30,
    borderColor:"red",
    borderWidth: 1,
  },

  signoutBtnTouchable:{
    flex: 1,
    justifyContent: 'flex-end',
    marginBottom: -10,
    width: "100%",

  },

  signoutTextBtn:{
    color:"red",
    fontWeight: "bold",
  },

});

// export default ProfileScreen;
