import React,{useState,useEffect} from 'react';
import { StyleSheet, View, Text,TouchableOpacity,TouchableWithoutFeedback,Keyboard,TextInput,Switch,KeyboardAvoidingView,ScrollView,Platform } from 'react-native';
import { Ionicons,Entypo,SimpleLineIcons,MaterialCommunityIcons,FontAwesome5,MaterialIcons } from '@expo/vector-icons';
import DateTimePickerModal from "react-native-modal-datetime-picker";
import { Overlay,Header } from 'react-native-elements';
import { useDispatch,useSelector } from 'react-redux';

import * as calendarActions from '../../../../store/actions/Calendar';

import LoadingScreen from '../../LoadingScreen';


const AddToCalendarScreen = ({navigation,route}) => {

    const token = useSelector((state) => state.authen.token);
    const allUserChoose = useSelector((state) => state.calendar.allUserChoose);

    const [isLoading,setLoading] = useState(false);

    const gmtDiff = 25200000;

    const getCurrentTimestamp=()=>{
        return Date.now();
    };

     //Handle for current time
     const getCurrentDay = (timestamp)=>{
        var today = new Date(timestamp); 
        var day= today.getDate();
        return day;
    };

    const getCurrentMonth =(timestamp)=> {
        var today = new Date(timestamp);
        var month = today.getMonth() + 1;
        return month; 
    };

    const getCurrentYear = (timestamp) => {
        var today = new Date(timestamp); 
        var year= today.getFullYear();
        return year;
    }
    
    const addZero=(i) =>{
        if (i < 10) {
            i = "0" + i;
        }
        return i;
    }
    
    const getCurrentTime = (timestamp) => {
        var d = new Date(timestamp);
        var h = addZero(d.getHours());
        var m = addZero(d.getMinutes());
        return h + ":" + m;
    }


     //Get all Guest name add to this calendar
     const getAllGuestName = () => {
        const tmp =[];
        for (const key in allUserChoose){
            tmp.push(allUserChoose[key].HoTen);
        }
        return tmp;
    };

    //Get all Email name add to this calendar
    const getAllGuestEmail = () => {
        const tmp =[];
        for (const key in allUserChoose){
            tmp.push(allUserChoose[key].Email);
        }
        return tmp;
    };
    

    const [startTime,setStartTime] = useState(getCurrentTime(getCurrentTimestamp()));
    const [endTime,setEndTime] =useState(getCurrentTime(getCurrentTimestamp()+3600*1000));

    const [day,setDay] = useState(getCurrentYear(getCurrentTimestamp())+'-'+addZero(getCurrentMonth(getCurrentTimestamp()))+'-'+addZero(getCurrentDay(getCurrentTimestamp())));
    const [startTimeConvert,setStartTimeConvert] = useState(day+'T'+startTime);
    const [endTimeConvert,setEndTimeConvert] = useState(day+'T'+endTime);
    // const [startTimeConvert,setStartTimeConvert] = useState(day+'T'+startTime);
    // const [endTimeConvert,setEndTimeConvert] = useState(day+'T'+endTime);

    const [title,setTitle]=useState(route.params.nameEvent);
    

    const [startTimestamp,setStartTimestamp]=useState(getCurrentTimestamp());
    const [endTimestamp,setEndTimestamp]=useState(getCurrentTimestamp()+3600*1000);
    const [isEnabled, setIsEnabled] = useState(false);

    const [isDatePickerVisible, setDatePickerVisibility] = useState(false);
    const [isStartTimePickerVisible, setStartTimePickerVisibility] = useState(false);
    const [isEndTimePickerVisible, setEndTimePickerVisibility] = useState(false);

    const [visibleOverlayAddTypeEvent, setVisibleOverlayAddTypeEvent] = useState(false);
    const [visibleOverlayAddColor, setVisibleOverlayAddColor] = useState(false);
    const [visibleOverlayRemindNoti, setVisibleOverlayRemindNoti] = useState(false);


    const [typeEvent,setTypeEvent]= useState('Công việc');
    const [colorEvent,setColorEvent] =useState('');
    const [urlEvent,setUrlEvent] =useState(route.params.urlEvent);
    const [decriptionEvent,setDecriptionEvent] =useState(route.params.decriptionEvent);
    const [timestampRemindNoti,setTimestampRemindNoti] = useState(startTimestamp);
    const [labelRemindNoti,setLabelRemindNoti] = useState('');

    const [listGuestEmail,setGuestEmail] = useState(getAllGuestEmail());
    const [listGuestName,setGuestName] = useState(getAllGuestName());


    const dispatch=useDispatch();

    const toggleOverlayAddTypeEvent = () => {
        setVisibleOverlayAddTypeEvent(!visibleOverlayAddTypeEvent);
    };

    const toggleOverlayAddColor = () => {
        setVisibleOverlayAddColor(!visibleOverlayAddColor);
    };

    const toggleOverlayAddRemindNoti = () => {
        setVisibleOverlayRemindNoti(!visibleOverlayRemindNoti);
    };


    const toggleSwitch = () => {
        setIsEnabled(previousState => !previousState);
        if(Platform.OS === 'ios'){
            if(!isEnabled){
                console.log(day);
                setStartTimeConvert(day+'T'+'00:00:00');
                setEndTimeConvert(day+'T'+'23:59:00');
                setStartTimestamp(new Date(day+'T'+'00:00:00').getTime());
                setEndTimestamp(new Date(day+'T'+'23:59:00').getTime());
    
            }
            else{
                setStartTimeConvert(day+'T'+startTime);
                setEndTimeConvert(day+'T'+endTime);
                setStartTimestamp(new Date(day+'T'+startTime).getTime());
                setEndTimestamp(new Date(day+'T'+endTime).getTime());
            }
        }
        else if (Platform.OS === 'android'){
            if(!isEnabled){
                console.log(day);
                setStartTimeConvert(day+'T'+'00:00:00');
                setEndTimeConvert(day+'T'+'23:59:00');
                setStartTimestamp(new Date(day+'T'+'00:00:00').getTime() - gmtDiff);
                setEndTimestamp(new Date(day+'T'+'23:59:00').getTime() - gmtDiff);
    
            }
            else{
                setStartTimeConvert(day+'T'+startTime);
                setEndTimeConvert(day+'T'+endTime);
                setStartTimestamp(new Date(day+'T'+startTime).getTime() - gmtDiff);
                setEndTimestamp(new Date(day+'T'+endTime).getTime() - gmtDiff);
            }
        }
    };

    //Handle for date event
    const showDatePicker = () => {
        setDatePickerVisibility(true);
    };
    
    const hideDatePicker = () => {
        setDatePickerVisibility(false);
    };

    const handleDateConfirm = (a) => {
        hideDatePicker();
        //const x=date.getFullYear()+'-'+(date.getMonth()+1)+'-'+date.getDate();
        //const x = moment("12-25-1995", "YYYY-MM-DD");
        //console.log(day);
        let date = new Date(a);
        let year = date.getFullYear();
        let month = date.getMonth()+1;
        let dt = date.getDate();

        console.log(new Date('2021-07-08T21:31:00').getTime());

        if (dt < 10) {
            dt = '0' + dt;
        }
        if (month < 10) {
            month = '0' + month;
        }

        setDay(year+'-' + month+'-'+dt);
        if(Platform.OS === 'ios'){
            if(!isEnabled){
                setStartTimeConvert(year+'-' + month+'-'+dt+'T'+startTime);
                setEndTimeConvert(year+'-' + month+'-'+dt+'T'+endTime);
                setStartTimestamp(new Date(year+'-' + month+'-'+dt+'T'+startTime).getTime());
                setEndTimestamp(new Date(year+'-' + month+'-'+dt+'T'+endTime).getTime());
            }
            else{
                setStartTimeConvert(year+'-' + month+'-'+dt+'T'+'00:00:00');
                setEndTimeConvert(year+'-' + month+'-'+dt+'T'+'23:59:00');
                setStartTimestamp(new Date(year+'-' + month+'-'+dt+'T'+'00:00:00').getTime());
                setEndTimestamp(new Date(year+'-' + month+'-'+dt+'T'+'23:59:00').getTime());
            }
        }
        else if (Platform.OS === 'android'){
            if(!isEnabled){
                setStartTimeConvert(year+'-' + month+'-'+dt+'T'+startTime);
                setEndTimeConvert(year+'-' + month+'-'+dt+'T'+endTime);
                setStartTimestamp(new Date(year+'-' + month+'-'+dt+'T'+startTime).getTime() - gmtDiff);
                setEndTimestamp(new Date(year+'-' + month+'-'+dt+'T'+endTime).getTime() - gmtDiff);
            }
            else{
                setStartTimeConvert(year+'-' + month+'-'+dt+'T'+'00:00:00');
                setEndTimeConvert(year+'-' + month+'-'+dt+'T'+'23:59:00');
                setStartTimestamp(new Date(year+'-' + month+'-'+dt+'T'+'00:00:00').getTime() - gmtDiff);
                setEndTimestamp(new Date(year+'-' + month+'-'+dt+'T'+'23:59:00').getTime() - gmtDiff);
            }
        }
    };

    //Handle for start time event
    const showStartTimePicker = () => {
        setStartTimePickerVisibility(true); 
    };
    
    const hideStartTimePicker = () => {
        setStartTimePickerVisibility(false);
    };

    const handleStartConfirm = (date) => {
        hideStartTimePicker();
        //console.log(day);
        //console.log(Date.now());

        //setStartTime(dayConvert);
        setStartTimeConvert(day+'T'+addZero(date.getHours())+':'+addZero(date.getMinutes())+':00');


        setStartTime(addZero(date.getHours())+':'+addZero(date.getMinutes()))
        //console.warn("A date has been picked: ", date);
        if(Platform.OS === 'ios'){
            setStartTimestamp(new Date(day+'T'+addZero(date.getHours())+':'+addZero(date.getMinutes())+':00').getTime());
        }
        else if(Platform.OS === 'android'){
            setStartTimestamp(new Date(day+'T'+addZero(date.getHours())+':'+addZero(date.getMinutes())+':00').getTime() - gmtDiff);
        }
    };


    //Handle for end time event
    const showEndTimePicker = () => {
        setEndTimePickerVisibility(true);
    };
    
    const hideEndTimePicker = () => {
        setEndTimePickerVisibility(false);
    };

    const handleEndConfirm = (date) => {
        hideEndTimePicker();
        //console.warn("A date has been picked: ", date);
        //setEndTimeConvert(day+'T'+addZero(date.getHours())+':'+addZero(date.getMinutes())+':00');
        //console.log(new Date(endTimeConvert).getTime());

        setEndTimeConvert(day+'T'+addZero(date.getHours())+':'+addZero(date.getMinutes())+':00');

        setEndTime(addZero(date.getHours())+':'+addZero(date.getMinutes()));
        if(Platform.OS === 'ios'){
            setEndTimestamp(new Date(day+'T'+addZero(date.getHours())+':'+addZero(date.getMinutes())+':00').getTime());
        }
        else if (Platform.OS === 'android'){
            setEndTimestamp(new Date(day+'T'+addZero(date.getHours())+':'+addZero(date.getMinutes())+':00').getTime() - gmtDiff);
        }
        //console.log(checkValidDate());
    };

    //Handle Start and End Date
    const checkValidDate = () =>{
        //console.log(startTimestamp,endTimestamp);
        
        if(startTimestamp >= endTimestamp){
        return false;
        }
        return true;
    };

    const checkTitle = (value) =>{
        if(value.trim().length === 0){
            return false;
        }
        return true;
    };

    //Back button handler
    const backButtonHandler = ()=>{
        dispatch(calendarActions.addPeopleToCalendar([]));
        navigation.goBack();
    }

    //Handle for clickable add button
    const checkDisableAddButton =() =>{
        if(checkTitle(title) && checkValidDate()){
            return false;
        }
        return true;
    }

    //call Api post calendar
    const addNewEvent = async ()=>{
        console.log(getAllGuestEmail());        
        setLoading(true);

        var myHeaders = new Headers();
        myHeaders.append("Authorization", `bearer ${token}`);
        myHeaders.append("Content-Type", "application/json");

        var raw = JSON.stringify({
            "Title": title,
            "TypeEvent": typeEvent,
            "year": getCurrentYear(startTimestamp).toString(),
            "month": getCurrentMonth(startTimestamp).toString(),
            "day": getCurrentDay(startTimestamp).toString(),
            "StartHour": Math.floor(startTimestamp/1000),
            "EndHour": Math.floor(endTimestamp/1000),
            "desciptionText": decriptionEvent,
            "url": urlEvent,
            "UnderLine": false,
            "Italic": false,
            "Bold": false,
            "Color": colorEvent,
            "listguestEmail": getAllGuestEmail(),
            "listguestName": getAllGuestName(),
            "Notification": timestampRemindNoti
        });

        var requestOptions = {
            method: 'POST',
            headers: myHeaders,
            body: raw,
            redirect: 'follow'
        };

        fetch("https://hcmusemu.herokuapp.com/calendar/post", requestOptions)
        .then((response) => {
            const statusCode = response.status;
            const dataRes = response.json();
            return Promise.all([statusCode, dataRes]);
        }).then(([statusCode, dataRes])=>{
            console.log(raw);
            console.log(dataRes);
            if(statusCode === 200){
                //getAllActivitiesInMonth();
                //dispatch(calendarActions.addNewEventToCalendar());
                dispatch(calendarActions.addPeopleToCalendar([]));
                setLoading(false);
                navigation.goBack();
            }  
        }).catch(error => console.log('error', error));
    };

    return(
        <KeyboardAvoidingView
        keyboardVerticalOffset = {20} // adjust the value here if you need more padding

         style={styles.container}>
        <TouchableWithoutFeedback onPress={() =>{
            Keyboard.dismiss();
          }}>
         
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     
          <View>

        
         <Header containerStyle={{
                backgroundColor: 'white',
                justifyContent: 'space-around',
            }}
            leftComponent={
                <TouchableOpacity style={{flexDirection:'row'}} onPress={() =>{backButtonHandler()}}>
                    <Entypo name="chevron-thin-left" size={24} color="red" />
                    <Text style={headerStyle.textLeftComponent}>Hủy</Text>
                </TouchableOpacity>
            }
            centerComponent={
                <Text style={headerStyle.textCenterComponent}>Sự kiện mới</Text>
            }
            rightComponent={
              <TouchableOpacity disabled={checkDisableAddButton()}
                onPress={() =>{
                    addNewEvent();
                    // console.log(day);
                    // console.log(startTime);
                    // console.log(startTimestamp);
                    // console.log(endTimestamp);
                }}>
                    <Text style={[headerStyle.textRightComponent,{color: checkDisableAddButton() ? 'silver' : 'blue'}]}>
                        Thêm
                    </Text>
                </TouchableOpacity>
            }/>


            
                <ScrollView style={{flexGrow:1}}>

                
                <View style={styles.card}>
                    <TextInput style={styles.titleName} placeholder='Tiêu đề'
                    onChangeText={(title)=>{
                        //console.log(checkTitle(title));
                        setTitle(title);
                        }}>{title}</TextInput>
                </View> 

                <View style={[styles.card,{marginBottom:0}]}>
                    <View style={styles.date}>
                        <SimpleLineIcons name="clock" size={20} color="red" />
                        <Text style={styles.label}>Cả ngày</Text>
                        <Switch
                            style={[{ transform: [{ scaleX: .8 }, { scaleY: .8 }],marginTop:-5,marginRight:-10},styles.onTheRight]}
                            trackColor={{ false: "white", true: "green" }}
                            // thumbColor={isEnabled ? "#f5dd4b" : "#f4f3f4"}
                            ios_backgroundColor="white"
                            onValueChange={toggleSwitch}
                            value={isEnabled}
                            size="medium"/>  
                    </View>
                </View>

                <View style={[styles.card,{marginTop:0}]}>
                    <TouchableOpacity style={styles.date} onPress={showDatePicker}>
                        <Text style={[styles.label,{marginLeft:32}]}>Ngày</Text>
                        <View style={[styles.onTheRight,{flexDirection:'row'}]}>
                            <Text style={styles.label} >
                                {getCurrentDay(startTimestamp)+" tháng "+ getCurrentMonth(startTimestamp)+"," + getCurrentYear(startTimestamp)}    
                            </Text>

                        </View>
                    </TouchableOpacity>

                    {!isEnabled && <TouchableOpacity style={styles.date} onPress={showStartTimePicker}>
                        <Text style={[styles.label,{marginLeft:32}]}>Bắt đầu</Text>
                        <View style={[styles.onTheRight,{flexDirection:'row'}]}>
                            <Text style={styles.label} >
                                {startTime}                            
                            </Text>
                        </View>
                    </TouchableOpacity>}

                    {!isEnabled && <TouchableOpacity style={styles.date} onPress={showEndTimePicker}>
                        <Text style={[styles.label,{marginLeft:32}]}>Kết thúc</Text>
                        <View style={[styles.onTheRight,{flexDirection:'row'}]}>
                            <Text style={[styles.label,{ textDecorationLine: checkValidDate() ? 'none' : 'line-through'}]} >
                                {endTime}
                            </Text>
                        </View>
                    </TouchableOpacity>}

                </View>
                
                <TouchableOpacity style={[styles.card,{marginBottom:0}]} 
                onPress={() =>{
                    navigation.navigate('Add people to calendar',{
                        typeAction: 'Add Event',
                    })}}>
                    <View style={styles.date}>
                    <Ionicons name="people-outline" size={23} color="red" />
                        <Text style={styles.label}>Thêm người</Text>
                        <Text style={[styles.onTheRight,styles.showChooseOnTheRight,colorStyle.colorLabelOnTheRight]}>{allUserChoose.length}</Text>
                        <Entypo style={styles.onTheRight} name="chevron-thin-right" size={18} color="blue" />
                    </View>
                </TouchableOpacity> 

                <TouchableOpacity style={[styles.card,{marginTop:0,marginBottom:0}]} onPress={toggleOverlayAddTypeEvent}>
                    <View style={styles.date}>
                    <SimpleLineIcons name="event" size={21} color="red" />
                        <Text style={styles.label}>Loại sự kiện</Text>
                        <Text style={[styles.onTheRight,styles.showChooseOnTheRight,colorStyle.colorLabelOnTheRight]}>{typeEvent}</Text>
                        <Entypo style={styles.onTheRight} name="chevron-thin-right" size={18} color="blue" />
                    </View>
                </TouchableOpacity> 

                <TouchableOpacity style={[styles.card,{marginTop:0,marginBottom:0}]} onPress={toggleOverlayAddColor}>
                    <View style={styles.date}>
                    <Ionicons name="color-palette-outline" size={23 } color="red" />
                        <Text style={styles.label}>Màu sắc</Text>
                        <View style={[styles.onTheRight,colorStyle.SquareShapeView,styles.showChooseOnTheRight,{backgroundColor: colorEvent}]}/>
                        <Entypo style={styles.onTheRight} name="chevron-thin-right" size={18} color="blue" />
                    </View>
                </TouchableOpacity>

                <TouchableOpacity style={[styles.card,{marginTop:0}]} onPress={toggleOverlayAddRemindNoti}>
                    <View style={styles.date}>
                    <Ionicons name="ios-notifications-outline" size={23} color="red" />
                        <Text style={styles.label}>Thông báo</Text>
                        <Text style={[styles.onTheRight,styles.showChooseOnTheRight,colorStyle.colorLabelOnTheRight]}>{labelRemindNoti}</Text>
                        <Entypo style={styles.onTheRight} name="chevron-thin-right" size={18} color="blue" />
                    </View>
                </TouchableOpacity> 

                <View style={[styles.card,{marginBottom:0}]}>
                    <View style={styles.date}>
                    <SimpleLineIcons name="link" size={20} color="red" />
                    <TextInput style={[styles.label,{width:"100%",color:"blue",textDecorationLine:'underline'}]} placeholder="URL"
                    onChangeText={(url) => setUrlEvent(url)}>{urlEvent}</TextInput>
                    </View>
                </View> 

                <View style={[styles.card,{marginTop:0,marginBottom:10}]}>
                    <View style={styles.date}>
                        <SimpleLineIcons name="note" size={20} color="red" />
                        <TextInput style={[styles.label,{width:"95%",marginTop:-5,height:100}]} placeholder="Mô tả" multiline={true}
                        onChangeText={(decription) => setDecriptionEvent(decription)}>{decriptionEvent}</TextInput>
                    </View>
                </View> 

                
                <DateTimePickerModal
                    locale={'vi'}
                    isVisible={isDatePickerVisible}
                    mode="date"
                    date={new Date(startTimestamp)}
                    headerTextIOS="Chọn ngày"
                    cancelTextIOS="Huỷ bỏ"
                    confirmTextIOS="Xác nhận"
                    onConfirm={handleDateConfirm}
                    onCancel={hideDatePicker}
                    onHide={()=>{
                        checkValidDate();
                    }}/>

                <DateTimePickerModal
                    locale={'vi'}
                    isVisible={isStartTimePickerVisible && !isEnabled}
                    mode="time"
                    date={new Date(startTimestamp)}
                    headerTextIOS="Chọn thời gian bắt đầu"
                    cancelTextIOS="Huỷ bỏ"
                    confirmTextIOS="Xác nhận"
                    onConfirm={handleStartConfirm}
                    onCancel={hideStartTimePicker}
                    onHide={()=>{
                        checkValidDate();
                    }}/>

                <DateTimePickerModal
                    locale={'vi'}
                    isVisible={isEndTimePickerVisible && !isEnabled}
                    mode="time"
                    date={new Date(endTimestamp)}
                    headerTextIOS="Chọn thời gian kết thúc"
                    cancelTextIOS="Huỷ bỏ"
                    confirmTextIOS="Xác nhận"
                    onConfirm={handleEndConfirm}
                    onCancel={hideEndTimePicker}
                    onHide={()=>{
                        checkValidDate();
                    }}/>


                {isLoading && LoadingScreen()}


                <Overlay isVisible={visibleOverlayAddTypeEvent} onBackdropPress={toggleOverlayAddTypeEvent}>
                    <TouchableOpacity style={[styles.card,{marginBottom:0,marginTop:0,borderBottomWidth:0}]} 
                    onPress={() => {toggleOverlayAddTypeEvent();setTypeEvent('Công việc')}}>
                        <View style={styles.date}>
                        <MaterialCommunityIcons name="office-building" size={24} color="black" />
                        <Text style={styles.label}>Công việc</Text>
                        </View>
                    </TouchableOpacity> 

                    <TouchableOpacity style={[styles.card,{marginTop:0,marginBottom:0,borderBottomWidth:0}]} 
                    onPress={() => {toggleOverlayAddTypeEvent();setTypeEvent('Nhà')}}>
                        <View style={styles.date}>
                        <FontAwesome5 name="home" size={20} color="black" />
                        <Text style={styles.label}>Nhà</Text>
                        </View>
                    </TouchableOpacity>        

                    <TouchableOpacity style={[styles.card,{marginTop:0,marginBottom:0,borderBottomWidth:0}]} 
                    onPress={() => {toggleOverlayAddTypeEvent();setTypeEvent('Gia đình')}}>
                        <View style={styles.date}>
                        <MaterialIcons name="family-restroom" size={24} color="black" />
                        <Text style={styles.label}>Gia đình</Text>
                        </View>
                    </TouchableOpacity>

                    <TouchableOpacity style={[styles.card,{marginTop:0,marginBottom:0,borderBottomWidth:0}]} 
                    onPress={() => {toggleOverlayAddTypeEvent();setTypeEvent('Giải trí')}}>
                        <View style={styles.date}>
                        <Ionicons name="game-controller" size={24} color="black" />
                        <Text style={styles.label}>Giải trí</Text>
                        </View>
                    </TouchableOpacity> 

                    <TouchableOpacity style={[styles.card,{marginTop:0,marginBottom:0,borderBottomWidth:0}]} 
                    onPress={() => {toggleOverlayAddTypeEvent();setTypeEvent('Bạn bè')}}>
                        <View style={styles.date}>
                        <MaterialCommunityIcons name="party-popper" size={24} color="black" />
                        <Text style={styles.label}>Bạn bè</Text>
                        </View>
                    </TouchableOpacity> 

                    <TouchableOpacity style={[styles.card,{marginTop:0,marginBottom:0,borderBottomWidth:0}]} 
                    onPress={() => {toggleOverlayAddTypeEvent();setTypeEvent('Thể thao')}}>
                        <View style={[styles.date,]}>
                        <MaterialIcons name="sports" size={24} color="black" />
                        <Text style={styles.label}>Thể thao </Text>
                        </View>
                    </TouchableOpacity> 
                </Overlay>


                <Overlay isVisible={visibleOverlayAddColor} onBackdropPress={toggleOverlayAddColor}> 
                    <TouchableOpacity style={[styles.card,{marginTop:0,marginBottom:0,borderBottomWidth:0}]} 
                    onPress={() => {toggleOverlayAddColor();setColorEvent('#00BCD4')}}>
                        <View style={[styles.date,]}>
                            <View style={[colorStyle.SquareShapeView,{backgroundColor: '#00BCD4'}]}/>
                            <Text style={styles.label}> Xanh dương </Text>
                        </View>
                    </TouchableOpacity> 

                    <TouchableOpacity style={[styles.card,{marginTop:0,marginBottom:0,borderBottomWidth:0}]} 
                    onPress={() => {toggleOverlayAddColor();setColorEvent('#FF6666')}}>
                        <View style={[styles.date,]}>
                        <View style={[colorStyle.SquareShapeView,{backgroundColor: '#FF6666'}]}/>
                            <Text style={styles.label}> Đỏ </Text>
                        </View>
                    </TouchableOpacity>

                    <TouchableOpacity style={[styles.card,{marginTop:0,marginBottom:0,borderBottomWidth:0}]} 
                    onPress={() => {toggleOverlayAddColor();setColorEvent('#CCFF00')}}>
                        <View style={[styles.date,]}>
                        <View style={[colorStyle.SquareShapeView,{backgroundColor: '#CCFF00'}]}/>
                            <Text style={styles.label}> Vàng </Text>
                        </View>
                    </TouchableOpacity>

                    <TouchableOpacity style={[styles.card,{marginTop:0,marginBottom:0,borderBottomWidth:0}]} 
                    onPress={() => {toggleOverlayAddColor();setColorEvent('#669966')}}>
                        <View style={[styles.date,]}>
                        <View style={[colorStyle.SquareShapeView,{backgroundColor: '#669966'}]}/>
                            <Text style={styles.label}> Xanh lá </Text>
                        </View>
                    </TouchableOpacity>  

                    <TouchableOpacity style={[styles.card,{marginTop:0,marginBottom:0,borderBottomWidth:0}]} 
                    onPress={() => {toggleOverlayAddColor();setColorEvent('#FF99CC')}}>
                        <View style={[styles.date,]}>
                        <View style={[colorStyle.SquareShapeView,{backgroundColor: '#FF99CC'}]}/>
                            <Text style={styles.label}> Hồng </Text>
                        </View>
                    </TouchableOpacity>

                    <TouchableOpacity style={[styles.card,{marginTop:0,marginBottom:0,borderBottomWidth:0}]} 
                    onPress={() => {toggleOverlayAddColor();setColorEvent('#330099')}}>
                        <View style={[styles.date,]}>
                        <View style={[colorStyle.SquareShapeView,{backgroundColor: '#330099'}]}/>
                            <Text style={styles.label}> Tím </Text>
                        </View>
                    </TouchableOpacity>

                    <TouchableOpacity style={[styles.card,{marginTop:0,marginBottom:0,borderBottomWidth:0}]} 
                    onPress={() => {toggleOverlayAddColor();setColorEvent('#663300')}}>
                        <View style={[styles.date,]}>
                        <View style={[colorStyle.SquareShapeView,{backgroundColor: '#663300'}]}/>
                            <Text style={styles.label}> Nâu </Text>
                        </View>
                    </TouchableOpacity>  

                    <TouchableOpacity style={[styles.card,{marginTop:0,marginBottom:0,borderBottomWidth:0}]} 
                    onPress={() => {toggleOverlayAddColor();setColorEvent('#DDDDDD')}}>
                        <View style={[styles.date,]}>
                        <View style={[colorStyle.SquareShapeView,{backgroundColor: '#DDDDDD'}]}/>
                            <Text style={styles.label}> Xám </Text>
                        </View>
                    </TouchableOpacity>       
                </Overlay>

                    {/* Overlay for remind notification */}
                <Overlay isVisible={visibleOverlayRemindNoti} onBackdropPress={toggleOverlayAddRemindNoti}>
                    <TouchableOpacity style={[styles.card,{marginBottom:0,marginTop:0,borderBottomWidth:0}]} 
                    onPress={() => {toggleOverlayAddRemindNoti();
                        setLabelRemindNoti('Trước 15 phút');
                        setTimestampRemindNoti(Math.floor(startTimestamp/1000)-(60*15))}}
                    >
                        <View style={styles.date}>
                        <Text style={styles.label}>Trước 15 phút</Text>
                        </View>
                    </TouchableOpacity> 

                    <TouchableOpacity style={[styles.card,{marginTop:0,marginBottom:0,borderBottomWidth:0}]} 
                    onPress={() => {toggleOverlayAddRemindNoti();
                        setLabelRemindNoti('Trước 30 phút');
                        setTimestampRemindNoti(Math.floor(startTimestamp/1000)-(60*30))}}
                    >
                        <View style={styles.date}>
                        <Text style={styles.label}>Trước 30 phút</Text>
                        </View>
                    </TouchableOpacity>        

                    <TouchableOpacity style={[styles.card,{marginTop:0,marginBottom:0,borderBottomWidth:0}]} 
                    onPress={() => {toggleOverlayAddRemindNoti();
                        setLabelRemindNoti('Trước 1 giờ');
                        setTimestampRemindNoti(Math.floor(startTimestamp/1000)-(60*60))}}
                    >
                        <View style={styles.date}>
                            <Text style={styles.label}>Trước 1 giờ</Text>
                        </View>
                    </TouchableOpacity>

                    <TouchableOpacity style={[styles.card,{marginTop:0,marginBottom:0,borderBottomWidth:0}]} 
                    onPress={() => {toggleOverlayAddRemindNoti();
                        setLabelRemindNoti('Trước 2 giờ');
                        setTimestampRemindNoti(Math.floor(startTimestamp/1000)-(60*60*2))}}
                    >
                        <View style={styles.date}>
                        <Text style={styles.label}>Trước 2 giờ</Text>
                        </View>
                    </TouchableOpacity> 

                    <TouchableOpacity style={[styles.card,{marginTop:0,marginBottom:0,borderBottomWidth:0}]} 
                    onPress={() => {toggleOverlayAddRemindNoti();
                        setLabelRemindNoti('Trước 3 giờ');
                        setTimestampRemindNoti(Math.floor(startTimestamp/1000)-(60*60*3))}}
                    >
                        <View style={styles.date}>
                            <Text style={styles.label}>Trước 3 giờ</Text>
                        </View>
                    </TouchableOpacity> 

                    <TouchableOpacity style={[styles.card,{marginTop:0,marginBottom:0,borderBottomWidth:0}]} 
                    onPress={() => {toggleOverlayAddRemindNoti();
                        setLabelRemindNoti('Trước 4 giờ');
                        setTimestampRemindNoti(Math.floor(startTimestamp/1000)-(60*60*4))}}
                    >
                        <View style={[styles.date,]}>
                        <Text style={styles.label}>Trước 4 giờ </Text>
                        </View>
                    </TouchableOpacity> 
                </Overlay>
                

                </ScrollView>
            </View>
        </TouchableWithoutFeedback>
    </KeyboardAvoidingView>
    )
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
    },

    card: {
        width: '100%',
        backgroundColor:'white',
        borderBottomWidth:1,
        borderBottomColor: "#EEEEEE",
        marginVertical:20
    },

    titleName:{
        marginLeft: 48,
        marginVertical:10,
        fontSize:20
    },

    date:{
        flexDirection:'row',
        marginHorizontal:20,
        marginVertical:10,
    },

    label: {
        fontSize:18,
        marginLeft:15
    },

    onTheRight: {
        position: 'absolute',
        right: 0
    },

    showChooseOnTheRight:{
        marginRight:20,
    }
});

const colorStyle = StyleSheet.create({
    SquareShapeView: { 
        width: 20,
        height: 20,
        borderRadius:7,
    },

    colorLabelOnTheRight: {
        fontSize:18,
        color:'#DDDDDD',
    }
});

const headerStyle = StyleSheet.create({
    textRightComponent:{
        fontSize:19,
        marginHorizontal:5
    },

    textLeftComponent:{
        color:'red',
        fontSize:19,
    },

    textCenterComponent:{
        color: 'black',
        fontSize:17,
        fontWeight:'bold',
        marginTop:2
    }
});

export default AddToCalendarScreen;
